  color: #ffffff;
  font-family: "Montserrat", sans-serif;
  font-size: 28px;
  font-weight: 400;
  line-height: 42px;
  text-align: center;
}
.header__arrow {
  color: #fff;
  font-size: 48px;
  font-weight: 400;
  line-height: 40px;
  position: absolute;
  bottom: 50px;
